static mylib::Size_Type array_size(mylib::Array *a)
{ mylib::Size_Type p;
  int       i;
  p = 1;
  for (i = 0; i < a->ndims; i++)
    p *= a->dims[i];
  return (p);
}

mylib::boolean mylib::Same_Shape(AForm *a, AForm *b)
{ int         i;
  mylib::boolean     eq;
  int         andims, bndims;
  mylib::Dimn_Type  *adims, *bdims;
  mylib::Coordinate *ashape, *bshape;

  if (Is_Array(a))
    { andims = ((mylib::Array *) a)->ndims;
      adims  = ((mylib::Array *) a)->dims;
      ashape = NULL;
    }
  else
    { if (Is_Slice(a))
        ashape = AForm_Shape(a);
      else
        ashape = Frame_Shape(a);
      andims = ashape->dims[0];
      adims  = ADIMN(ashape);
    }

  if (Is_Array(b))
    { bndims = ((mylib::Array *) b)->ndims;
      bdims  = ((mylib::Array *) b)->dims;
      bshape = NULL;
    }
  else
    { if (Is_Slice(b))
        bshape = AForm_Shape(b);
      else
        bshape = Frame_Shape(b);
      bndims = bshape->dims[0];
      bdims  = ADIMN(bshape);
    }
    
  eq = 0;
  if (andims == bndims)
    { for (i = 0; i < andims; i++)
        if (adims[i] != bdims[i])
          break;
      eq = (i >= andims);
    }

  if (Is_Slice(a))
    Free_Array(ashape);
  if (Is_Slice(b))
    Free_Array(bshape);

  return (eq);
}

mylib::boolean mylib::Same_Type(AForm *a, AForm *b)
{ if (AForm_Array(a)->type != AForm_Array(b)->type)
    return (0);
  return (mylib::Same_Shape(a,b));
}


