static mylib::Dimn_Type  Coord_Ndim, Coord_Max = 0;
static mylib::Dimn_Type *Coord_Dims;
static mylib::Array_Kind Coord_Kind;

static pthread_mutex_t Coord_Mutex = PTHREAD_MUTEX_INITIALIZER;

void mylib::Set_Coord_Basis(Coordinate *_F(point), mylib::Array_Kind kind)
{ pthread_mutex_lock(&Coord_Mutex);
  if (point == NULL)
    { Coord_Dims = NULL;
      Coord_Ndim = 0;
      return;
    }
  if (point->ndims != 1 || point->type != DIMN_TYPE)
    { fprintf(stderr,"Coordinate is not an unsigned integer vector (Set_Coord_Basis)\n");
      exit (1);
    }
  Coord_Ndim = point->dims[0];
  Coord_Kind = kind;
  if (Coord_Ndim > Coord_Max)
    { Coord_Max  = Coord_Ndim + 10;
      Coord_Dims = (mylib::Dimn_Type*) Guarded_Realloc(Coord_Dims,
                                   sizeof(mylib::Dimn_Type)*((size_t) Coord_Max),
                                   "Set_Coord_Basis");
    }
  memcpy(Coord_Dims,ADIMN(point),sizeof(mylib::Dimn_Type)*((size_t) Coord_Ndim));
  pthread_mutex_unlock(&Coord_Mutex);
  Free_Array(point);
}

mylib::Coordinate *_G(mylib::Get_Coord_Basis)(mylib::Array_Kind *kind)
{ mylib::Dimn_Type  dim[1];
  mylib::Array     *point;

  if (Coord_Dims == NULL)
    return (NULL);
  pthread_mutex_lock(&Coord_Mutex);
  dim[0] = Coord_Ndim;
  point  = Make_Array(PLAIN_KIND,DIMN_TYPE,1,dim);
  memcpy(ADIMN(point),Coord_Dims,sizeof(mylib::Dimn_Type)*((size_t) Coord_Ndim));
  *kind = Coord_Kind;
  pthread_mutex_unlock(&Coord_Mutex);
  return (point);
}

void mylib::Use_Array_Basis(mylib::Array *a)
{ pthread_mutex_lock(&Coord_Mutex);
  Coord_Ndim = a->ndims;
  Coord_Kind = a->kind;
  if (Coord_Ndim > Coord_Max)
    { Coord_Max  = Coord_Ndim + 10;
      Coord_Dims = (mylib::Dimn_Type*) Guarded_Realloc(
            Coord_Dims,sizeof(mylib::Dimn_Type)*((size_t) Coord_Max),
            "Use_Array_Basis");
    }
  memcpy(Coord_Dims,a->dims,sizeof(mylib::Dimn_Type)*((size_t) Coord_Ndim));
  pthread_mutex_unlock(&Coord_Mutex);
}

mylib::Coordinate *_G(mylib::Coord)(mylib::string list)
{ mylib::Array     *coord;
  mylib::Dimn_Type  dim[1], *c;
  int        i, n;
  char      *b, *e;

  n = 1;
  for (i = 0; i < (int) strlen(list); i++)
    if (list[i] == ',')
      n += 1;

  dim[0] = n;
  coord  = mylib::Make_Array(PLAIN_KIND,DIMN_TYPE,1,dim);

  c = ADIMN(coord);
  b = list;
  while (1)
    { c[--n] = (mylib::Dimn_Type) strtol(b,&e,10);
      if (b == e)
        { fprintf(stderr,"Not a valid constant mylib::Coordinate list (Coord)\n");
          exit (1);
        }
      b = e;
      if (*b == '\0')
        break;
      if (*b++ != ',')
        { fprintf(stderr,"Not a valid constant mylib::Coordinate list (Coord)\n");
          exit (1);
        }
    }

  return (coord);
}

