mylib::Slice *_G(mylib::Make_Slice)(mylib::Array *_I(target), mylib::Coordinate *_S(beg), mylib::Coordinate *_S(end))
{ Slicer    *slice;
  mylib::Dimn_Type *ecrd = ADIMN(end);
  mylib::Dimn_Type *bcrd = ADIMN(beg);
  mylib::Dimn_Type *dims;
  int        i, ndims;

  if (beg->ndims != 1 || beg->type != DIMN_TYPE)
    { fprintf(stderr,"Beg is not a mylib::Coordinate vector (Make_Slice)\n");
      exit (1);
    }
  if (end->ndims != 1 || end->type != DIMN_TYPE)
    { fprintf(stderr,"End is not a mylib::Coordinate vector (Make_Slice)\n");
      exit (1);
    }
  if (Array_Refcount(beg) != 1)
    { fprintf(stderr,"Beg is not subsumable, has a reference count > 1 (Make_Slice)\n");
      exit (1);
    }
  if (Array_Refcount(end) != 1)
    { fprintf(stderr,"End is not subsumable, has a reference count > 1 (Make_Slice)\n");
      exit (1);
    }

  ndims = (int) beg->size;
  if (target->ndims != ndims && (target->ndims-1 != ndims || target->kind == PLAIN_KIND))
    { fprintf(stderr,"Target and mylib::Coordinate dimensionality do not match (Make_Slice)\n");
      exit (1);
    }
  if (end->size != ndims)
    { fprintf(stderr,"Begin and end mylib::Coordinate dimensionality do not match (Make_Slice)\n");
      exit (1);
    }

  if (ndims == target->ndims-1 && target->kind == COMPLEX_KIND)
    dims = target->dims+1;
  else
    dims = target->dims;
  for (i = 0; i < ndims; i++)
    { if (bcrd[i] > ecrd[i])
        { fprintf(stderr,"beg is not before end in target array (Make_Slice)\n");
          exit (1);
        }
      if (ecrd[i] >= dims[i])
        { fprintf(stderr,"end is not in basis of target array (Make_Slice)\n");
          exit (1);
        }
      if (bcrd[i] < 0)
        { fprintf(stderr,"beg is not in basis of target array (Make_Slice)\n");
          exit (1);
        }
    }
  dims = target->dims;

  if (ndims == target->ndims-1)
    { ndims += 1;
      if (target->kind == COMPLEX_KIND)
        { PrependCoord(beg,0);
          PrependCoord(end,1);
        }
      else
        { AppendCoord(0,beg);
          AppendCoord(kind_size[target->kind]-1,end);
        }
      bcrd = ADIMN(beg);
      ecrd = ADIMN(end);
    }

  slice = new_slicer(ndims*SIZEOF(mylib::Size_Type),NCC("Make_Slice"));

  slice->kind    = SLICE_KIND;
  slice->trg_ref = mylib::Inc_Array(target);
  slice->beg     = beg;
  slice->end     = end;

  slice->ndims = ndims;
  slice->size  = 1;
  for (i = 0; i < ndims; i++)
    slice->size *= (ecrd[i] - bcrd[i]) + 1;

  slice->cnt = Copy_Array(beg);

  { mylib::Size_Type *dinc = slice->dnc;
    mylib::Size_Type  offset, outer;

    offset  = 0;
    outer   = 1;
    for (i = 0; i < ndims; i++)
      { dinc[i] = outer-offset;
        offset += (ecrd[i]-bcrd[i])*outer;
        outer  *= dims[i];
      }

    slice->p = Coord2IdxA(target,Inc_Array(beg));
    slice->clip = -1;
  }

  slice->acnt = ADIMN(slice->cnt);
  slice->bcrd = bcrd;
  slice->ecrd = ecrd;

  return ((Slice *) slice);
}

