static void frame_invert_uint64(int d, mylib::int64 q, mylib::int64 t, mylib::uint64  *o, mylib::uint64  *a, Frame_Args *args)
{ mylib::int64 j, k, m, x;
  mylib::int64 e = args->fecrd[d];
  mylib::int64 b = args->fbcrd[d];
  mylib::int64 f = args->fidim[d];

  q  = q*f;
  t  = t*args->ffdim[d];
  m  = ((f-1) << 1);

  if (d == 0)
    { mylib::uint64    w, ab, ae;

      a += q;
      o += t;
      ab = 2*a[0];
      ae = 2*a[f-1];

      j = b;
      if (b < 0)
        { j  = -b;
          x  = (j-1)/(f-1)+1;
          if (x % 2)
            w = ((mylib::uint64) (x/2))*(ab - ae) + ab;
          else
            w = ((mylib::uint64) (x/2))*(ab - ae);
          x = -min_int64(e,0);
          for (k = (j-1) % m + 1; j > x; j--)
            if (k >= f)
              { *o++ = (mylib::uint64) (w + a[m-k]);
                if (k-- == f)
                  w += ae;
              }
            else
              { *o++ = (mylib::uint64) (w - a[k]);
                if (k-- == 1)
                  { w -= ab;
                    k  = m;
                  }
              }
          if (e <= 0)
            return;
        }

      for (x = min_int64(e,f); j < x; j++)
        *o++ = (mylib::uint64) a[j];

      if (j < e)
        { x = (j-f)/(f-1) + 1;
          if (x % 2)
            w = ((mylib::uint64) (x/2))*(ae-ab) + ae;
          else
            w = ((mylib::uint64) (x/2))*(ae-ab);
          for (k = m - (j-1) % m; j < e; j++)
            if (k-- >= f)
              { *o++ = (mylib::uint64) (w + a[m-k]);
                if (k < f)
                  w += ae;
              }
            else
              { *o++ = (mylib::uint64) (w - a[k]);
                if (k == 0)
                  { w -= ab;
                    k  = m;
                  }
              }
        }
    }

  else
    { mylib::int64 lx, rx, y, S;
      mylib::uint64    *L, *R, *F;

      d -= 1;
      t -= b;

      S = args->fdvol[d];
      F = o;
      L = (mylib::uint64  *) args->fibuf[d];
      R = L + S;

      frame_invert_uint64(d,q,0,L,a,args);
      frame_invert_uint64(d,q+(f-1),0,R,a,args);

      j = b;
      if (b < 0)
        { j  = -b;
          x  = (j-1)/(f-1)+1;
          if (x % 2)
            { lx = x+1;
              rx = -(x-1);
            }
          else
            { lx = x;
              rx = -x;
            }
          x = -min_int64(e,0);
          F = o + (t+b)*S;
          for (k = (j-1) % m + 1; j > x; j--)
            if (k >= f)
              { frame_invert_uint64(d,q + (m-k),t - j,o,a,args);

                for (y = 0; y < S; y++)
                  F[y] = (mylib::uint64) (lx*((mylib::int64) L[y]) + rx*((mylib::int64) R[y]) + ((mylib::int64) F[y]));
                F += S;

                if (k-- == f)
                  rx += 2;
              }
            else
              { frame_invert_uint64(d,q + k,t - j,o,a,args);

                for (y = 0; y < S; y++)
                  F[y] = (mylib::uint64) (lx*((mylib::int64) L[y]) + rx*((mylib::int64) R[y]) - ((mylib::int64) F[y]));
                F += S;

                if (k-- == 1)
                  { lx -= 2;
                    k   = m;
                  }
              }
          if (e <= 0)
            return;
        }

      for (x = min_int64(e,f); j < x; j++)
        frame_invert_uint64(d,q + j,t + j,o,a,args);

      if (j < e)
        { x = (j-f)/(f-1) + 1;
          if (x % 2)
            { rx = x+1;
              lx = -(x-1);
            }
          else
            { rx = x;
              lx = -x;
            }
          F = o + (t+j)*S;
          for (k = m - (j-1) % m; j < e; j++)
            if (k-- >= f)
              { frame_invert_uint64(d,q + (m-k),t + j,o,a,args);

                for (y = 0; y < S; y++)
                  F[y] = (mylib::uint64) (lx*((mylib::int64) L[y]) + rx*((mylib::int64) R[y]) + ((mylib::int64) F[y]));
                F += S;

                if (k < f)
                  rx += 2;
              }
            else
              { frame_invert_uint64(d,q + k,t + j,o,a,args);

                for (y = 0; y < S; y++)
                  F[y] = (mylib::uint64) (lx*((mylib::int64) L[y]) + rx*((mylib::int64) R[y]) - ((mylib::int64) F[y]));
                F += S;

                if (k == 0)
                  { lx -= 2;
                    k   = m;
                  }
              }
        }
    }
}

