static void expand_invert_int32(int d, int out, mylib::int64 q, mylib::int64 t, mylib::int32 *o, mylib::int32 *a, Frame_Args *args)
{ mylib::int64 j, k, m, x;
  mylib::int64 e = args->fecrd[d];
  mylib::int64 b = args->fbcrd[d];
  mylib::int64 f = args->ffdim[d];

  q  = q*args->fidim[d] - b;
  t  = t*args->fidim[d];
  m  = ((f-1) << 1);

  if (d == 0)
    { mylib::int64   w, ab, ae;

      a += q;
      o += t;
      ab = 2*a[0];
      ae = 2*a[f-1];

      j = b;
      if (b < 0)
        { j  = -b;
          x  = (j-1)/(f-1)+1;
          if (x % 2)
            w = ((mylib::int64) (x/2))*(ab - ae) + ab;
          else
            w = ((mylib::int64) (x/2))*(ab - ae);
          x = -min_int64(e,0);
          for (k = (j-1) % m + 1; j > x; j--)
            if (k >= f)
              { *o++ = (mylib::int32) (w + a[m-k]);
                if (k-- == f)
                  w += ae;
              }
            else
              { *o++ = (mylib::int32) (w - a[k]);
                if (k-- == 1)
                  { w -= ab;
                    k  = m;
                  }
              }
          if (e <= 0)
            return;
        }

      if (out)
        for (x = min_int64(e,f); j < x; j++)
          *o++ = (mylib::int32) a[j];
      else
        { o += f;
          j  = f;
        }

      w = ae;
      for (k = f-1; j < e; j++)
        if (k-- >= f)
          { *o++ = (mylib::int32) (w + a[m-k]);
            if (k < f)
              w += ae;
          }
        else
          { *o++ = (mylib::int32) (w - a[k]);
            if (k == 0)
              { w -= ab;
                k  = m;
              }
          }
    }

  else
    { mylib::int64 lx, rx, y, S;
      mylib::int32  *L, *R, *F;

      d -= 1;
      t -= b;

      expand_invert_int32(d,out,q,t,o,a,args);
      expand_invert_int32(d,out,q+(f-1),t+(f-1),o,a,args);

      S = args->fdvol[d];
      L = o + t*S;
      R = o + (t+(f-1))*S;

      j = b;
      if (b < 0)
        { j  = -b;
          x  = (j-1)/(f-1)+1;
          if (x % 2)
            { lx = x+1;
              rx = -(x-1);
            }
          else
            { lx = x;
              rx = -x;
            }
          x = -min_int64(e,0);
          F = L + b*S;
          for (k = (j-1) % m + 1; j > x; j--)
            if (k >= f)
              { expand_invert_int32(d,1,q + (m-k),t - j,o,a,args);

                for (y = 0; y < S; y++)
                  F[y] = (mylib::int32) (lx*L[y] + rx*R[y] + F[y]);
                F += S;

                if (k-- == f)
                  rx += 2;
              }
            else
              { expand_invert_int32(d,1,q + k,t - j,o,a,args);

                for (y = 0; y < S; y++)
                  F[y] = (mylib::int32) (lx*L[y] + rx*R[y] - F[y]);
                F += S;

                if (k-- == 1)
                  { lx -= 2;
                    k   = m;
                  }
              }
          if (e <= 0)
            return;
        }

      for (j = 1; j < f-1; j++)
        expand_invert_int32(d,out,q + j,t + j,o,a,args);

      rx = 2;
      lx = 0;
      F  = R + S;
      k  = f-1;
      for (j = f; j < e; j++)
        if (k-- >= f)
          { expand_invert_int32(d,1,q + (m-k),t + j,o,a,args);

            for (y = 0; y < S; y++)
              F[y] = (mylib::int32) (lx*L[y] + rx*R[y] + F[y]);
            F += S;

            if (k < f)
              rx += 2;
          }
        else
          { expand_invert_int32(d,1,q + k,t + j,o,a,args);

            for (y = 0; y < S; y++)
              F[y] = (mylib::int32) (lx*L[y] + rx*R[y] - F[y]);
            F += S;

            if (k == 0)
              { lx -= 2;
                k   = m;
              }
          }
    }
}


