      for (j = f; j < e; j++)
        if (k-- >= f)
          { expand_invert_float64(d,1,q + (m-k),t + j,o,a,args);

            for (y = 0; y < S; y++)
              F[y] = (mylib::float64) (lx*L[y] + rx*R[y] + F[y]);
            F += S;

            if (k < f)
              rx += 2;
          }
        else
          { expand_invert_float64(d,1,q + k,t + j,o,a,args);

            for (y = 0; y < S; y++)
              F[y] = (mylib::float64) (lx*L[y] + rx*R[y] - F[y]);
            F += S;

            if (k == 0)
              { lx -= 2;
                k   = m;
              }
          }
