mylib::Array *convert_image(mylib::Array *array, mylib::Array_Kind kind, Value_Type type, int scale, int in_place)
{ Variable_Scale var;
  mylib::Array         *rez;
  int            atype;

  var.dscale = 1.;
  var.tshift = array->scale - scale;

  atype = array->type;
  if (type >= mylib::FLOAT32_TYPE)
    { if (atype <= mylib::UINT64_TYPE)
        { mylib::uint64  base = (((mylib::uint64) 1) << (array->scale-1));
          var.dscale = 1. / ((base-1) + base);
        }
      else if (atype <= mylib::INT64_TYPE)
        var.dscale = 1. / ((((mylib::uint64) 1) << (array->scale-1)) - 1);
    }
  else if (type >= mylib::INT8_TYPE)
    { if (atype >= mylib::FLOAT32_TYPE)
        var.dscale = (double) ((((mylib::uint64) 1) << (scale-1)) - 1);
      else if (atype <= mylib::UINT64_TYPE)
        var.tshift += 1;
    }
  else
    { if (atype >= mylib::FLOAT32_TYPE)
        { mylib::uint64  base = (((mylib::uint64) 1) << (scale-1));
          var.dscale = (double) ((base-1) + base);
        }
      else if (atype >= mylib::INT64_TYPE)
        var.tshift -= 1;
    }

  rez = convert_array(array,kind,type,scale,&var,in_place);

  return (rez);
}

