void check_clip_args(AForm *source, mylib::Coordinate *beg, mylib::Coordinate *end, string routine)
{ mylib::Coordinate *basis = AForm_Shape(source);
  mylib::Dimn_Type  *dims  = ADIMN(basis);
  mylib::Dimn_Type  *bcrd  = ADIMN(beg);
  mylib::Dimn_Type  *ecrd  = ADIMN(end);
  int         ndims = (int) end->size;
  int         i;

  if (ndims != beg->size)
    { fprintf(stderr,"Coordinates are not of same dimensionality (%s)\n",routine);
      exit (1);
    }
  if (basis->size != ndims && (basis->size != ndims+1 || AForm_Kind(source) == PLAIN_KIND))
    { fprintf(stderr,"Source and mylib::Coordinate dimensionality do not match (%s)\n",routine);
      exit (1);
    }
  
  if (ndims == basis->size-1 && AForm_Kind(source) == COMPLEX_KIND)
    dims += 1;
  for (i = 0; i < ndims; i++)
    { if (bcrd[i] > ecrd[i])
        { fprintf(stderr,"beg mylib::Coordinate is not before end mylib::Coordinate (%s)\n",routine);
          exit (1);
        }
      if (ecrd[i] >= dims[i])
        { fprintf(stderr,"end is not in basis of target array (%s)\n",routine);
          exit (1);
        }
    }

  Free_Array(basis);
}
 
mylib::Array *Clip_Array_Inplace(mylib::Array *_R(_M(source)), mylib::Coordinate *_F(beg), mylib::Coordinate *_F(end))
{ mylib::Coordinate *shape;
  mylib::Dimn_Type  *bcrd, *scrd, *dims, s;
  int         i, ndims;

  check_clip_args(source,beg,end,NCC("Clip_Array_Inplace"));

  ndims = (int) end->size;
  shape = Copy_Array(end);
  bcrd  = ADIMN(beg);
  scrd  = ADIMN(shape);
  for (i = 0; i < ndims; i++)
    scrd[i] -= (bcrd[i]-1);

  clip_array(source,source->data,Copy_Array(beg),Copy_Array(end));
  Free_Array(beg);
  Free_Array(end);

  dims = source->dims;
  if (source->ndims != ndims)
    { source->size = kind_size[source->kind];
      if (source->kind == COMPLEX_KIND)
        dims += 1;
    }
  else
    source->size = 1;
  for (i = 0; i < ndims; i++)
    { dims[i] = s = scrd[i];
      source->size *= s;
    }

  Free_Array(shape);

  return (source);
}
    

mylib::Array *_G(Clip_Array)(AForm *source, mylib::Coordinate *_F(beg), mylib::Coordinate *_F(end))
{ mylib::Coordinate *shape;
  mylib::Dimn_Type  *bcrd, *scrd;
  mylib::Array      *target;
  int         ndims, i;
  mylib::boolean     islice;
  mylib::Array      *a = AForm_Array(source);

  check_clip_args(source,beg,end,NCC("Clip_Array"));

  ndims = (int) end->size;

  islice = Is_Slice(source);
  if (islice)
    { mylib::Coordinate *gpnt = Copy_Array(beg);
      mylib::Coordinate *hpnt = Copy_Array(end);
      mylib::Coordinate *fpnt = Slice_First(source);
      mylib::Dimn_Type  *gcrd = ADIMN(gpnt);
      mylib::Dimn_Type  *hcrd = ADIMN(hpnt);
      mylib::Dimn_Type  *fcrd = ADIMN(fpnt);

      if (ndims != fpnt->size && AForm_Kind(source) == COMPLEX_KIND)
        fcrd += 1;
      for (i = 0; i < ndims; i++)
        { gcrd[i] += fcrd[i];
          hcrd[i] += fcrd[i];
        }
    
      Free_Array(beg);
      Free_Array(end);
      beg = gpnt;
      end = hpnt;
    }

  shape = Copy_Array(end);
  bcrd  = ADIMN(beg);
  scrd  = ADIMN(shape);
  for (i = 0; i < ndims; i++)
    scrd[i] -= (bcrd[i]-1);

  if (a->ndims == ndims)
    { target = Make_Array_With_Shape(PLAIN_KIND,a->type,shape);
      target->kind = AForm_Kind(source);
    }
  else
    target = Make_Array_With_Shape(AForm_Kind(source),a->type,shape);

  if (Is_Frame(source))
    clip_frame(source,target->data,Copy_Array(beg),Copy_Array(end));
  else if (islice)
    clip_array(a,target->data,beg,end);
  else
    clip_array(a,target->data,Copy_Array(beg),Copy_Array(end));

  if (!islice)
    { Free_Array(beg);
      Free_Array(end);
    }

  return (target);
}


