void check_expand_args(AForm *source, mylib::Coordinate *anchor, mylib::Coordinate *shape, string routine)
{ mylib::Coordinate *basis = AForm_Shape(source);
  mylib::Dimn_Type  *dims  = ADIMN(basis);
  mylib::Dimn_Type  *acrd  = ADIMN(anchor);
  mylib::Dimn_Type  *scrd  = ADIMN(shape);
  int         ndims = (int) anchor->size;
  int         i;

  if (anchor->size != shape->size || basis->size != shape->size)
    { fprintf(stderr,"Coordinates are not of same dimensionality (%s)\n",routine);
      fprintf(stderr," (Pad_Array_Inplace)\n");
      exit (1);
    }
  if (basis->size != ndims && (basis->size != ndims+1 || AForm_Kind(source) == PLAIN_KIND))
    { fprintf(stderr,"Source and mylib::Coordinate dimensionality do not match (%s)\n",routine);
      exit (1);
    }

  if (ndims == basis->size-1 && AForm_Kind(source) == COMPLEX_KIND)
    dims += 1;
  for (i = 0; i < ndims; i++)
    { if (acrd[i] >= scrd[i])
        { fprintf(stderr,"anchor mylib::Coordinate is not inside shape (%s)\n",routine);
          exit (1);
        }
      if (acrd[i] + dims[i] > scrd[i])
        { fprintf(stderr,"source does not fit into shape when placed at anchor (%s)\n",routine);
          exit (1);
        }
    }

  Free_Array(basis);
}
 
mylib::Array *Pad_Array_Inplace(mylib::Array *_R(_M(source)), mylib::Coordinate *_F(anchor), mylib::Coordinate *_F(shape))
{ mylib::Coordinate *end;
  mylib::Dimn_Type  *ecrd, *acrd, *dims;
  mylib::Size_Type   size;
  int         i, ndims;

  check_expand_args(source,anchor,shape,NCC("Pad_Array_Inplace"));
  ndims = (int) anchor->size;

  end  = Copy_Array(shape);
  Free_Array(shape);

  size = source->size;
  ecrd = ADIMN(end);
  acrd = ADIMN(anchor);
  dims = source->dims;
  if (source->ndims != ndims)
    { source->size = kind_size[source->kind];
      if (source->kind == COMPLEX_KIND)
        dims += 1;
    }
  else
    source->size = 1;
  for (i = 0; i < ndims; i++)
    { int m = ecrd[i];
      ecrd[i] = acrd[i] + (dims[i]-1);
      dims[i] = m;
      source->size *= m;
    }

  allocate_array_data(source,array_dsize(source),NCC("Expland_Array_In_Place"));

  expand_array(source,source,size,Copy_Array(anchor),end);
  Free_Array(anchor);
  
  return (source);
}


mylib::Array *_G(Pad_Array)(AForm *source, mylib::Coordinate *_F(anchor), mylib::Coordinate *_F(shape))
{ mylib::Array      *target;
  mylib::Coordinate *end, *basis;
  mylib::Dimn_Type  *ecrd, *acrd, *dims;
  int         i, ndims;
  mylib::Array      *a = AForm_Array(source);
 
  check_expand_args(source,anchor,shape,NCC("Pad_Array"));
  ndims = (int) anchor->size;

  end  = Copy_Array(shape);
  Free_Array(shape);

  ecrd = ADIMN(end);
  acrd = ADIMN(anchor);

  if (a->ndims == ndims)
    { target = Make_Array(PLAIN_KIND,a->type,a->ndims,ecrd);
      target->kind = AForm_Kind(source);
    }
  else
    target = Make_Array(AForm_Kind(source),a->type,ndims,ecrd);

  basis = AForm_Shape(source);
  dims  = ADIMN(basis);
  if (a->ndims != ndims && AForm_Kind(source) == COMPLEX_KIND)
    dims += 1;
  for (i = 0; i < ndims; i++)
    ecrd[i] = acrd[i] + (dims[i]-1);
  Free_Array(basis);

  expand_array(target,source,AForm_Size(source),Copy_Array(anchor),end);
  Free_Array(anchor);

  return (target);
}


